import react from 'react';
import {Form,Button,Row,Col} from 'react-bootstrap';
import {useFormik} from 'formik';
import * as yup from 'yup'


const initialValues={
  location: '',
  destination: '',
  type: ''
}

const onSubmit= values =>{
  console.log('form data',values)
      }

      const validate=values =>{
        const errors={};

        if(!values.location){
               errors.location='Required'
        }

        if(!values.destination){
          errors.location= 'Required'
        }

        if(!values.type){
          errors.type='Required'
        }

        return errors
 }

 const validationSchema= Yup.object({
   location: Yup.string().required('Required!'),
   email: Yup.String().required('Required!'),
   type: Yup.string().required('Required!')
 })


const RentalForm = () => {

   const formik = useFormik({
     initialValues,
     onSubmit,
    validationSchema
   })

   console.log('Form Values', formik.values)
  return (
     <div>

<Form onSubmit={formik.handleSubmit}>
  <Row className="mb-3">
    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Source Location*</Form.Label>
      <Form.Control type="location" name="location" 
     onChange={formik.handleChange} value={formik.values.location} />
    </Form.Group>
    {formik.errors.location ? <div className='error'>{formik.errors.location}</div> : null }

    <Form.Group as={Col} controlId="formGridPassword">
      <Form.Label>Destination*</Form.Label>
      <Form.Control type="destination" name="destination"
       onChange={formik.handleChange} value={formik.values.destination}   />
    </Form.Group>
    {formik.errors.destination ? <div className='error'>{formik.errors.destination}</div> : null }
  </Row>

    <Form.Group as={Col} controlId="formGridState">
      <Form.Label>Enter Car type*</Form.Label>
      <Form.Select defaultValue="Hatchback" name="type"
       onChange={formik.handleChange} value={formik.values.type} >
        <option>HatchBack</option>
        <option>Sedan</option>
        <option>SUV</option>
      </Form.Select>
    </Form.Group>
    {formik.errors.type ? <div className='error'>{formik.errors.type}</div> : null }
 
    <Form.Group className="mb-3" controlId="formGridAddress1">
    <Form.Label>Number of Travellers</Form.Label>
    <Form.Control  />
  </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>

    </div>
  )
}

export default RentalForm
